.hidden {
    display: none;
}

.parent-container {
    position: absolute;
    width: 100%;
    height: 100%;
    // background-color: #303047;
    background: linear-gradient(
      to right bottom, 
      rgb(24, 27, 62), 
      rgb(0, 0, 21)
      );
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: "Nunito Sans", sans-serif;


    .sample-search {
      position: absolute;
      margin: 10px;
      top: 0;
      right: 0;
      color: #efe9ff;
      text-transform: uppercase;
    }
}

.recipe-wrapper {
    width: 80%;
    height: 90%;
    background-color: #d7d5d3;
    border-radius: 10px;
    display: flex;
    overflow: hidden;

    &__left {
      position: relative; //hays
        width: 35%;
        padding: 20px;

        header {
            position: relative;
            width: 100%;
            height: 40px;
            border-radius: 50px;

            form {
                width: 100%;
                height: 100%;
                position: relative;
                background-color: rgb(239, 233, 255);
                border-radius: 50px;
                display: flex;
                justify-content: center;
                align-items: center;

                input, button {
                    outline: none;
                    border: none;
                }

                input {
                    width: 70%;
                    height: 100%;
                    border-radius: 50px 0 0 50px;
                    background-color: rgb(239, 233, 255);
                    padding: .625rem;
                    padding: .9375rem;
                }
                button {
                    width: 30%;
                    height: 100%;
                    border-radius: 50px;
                    background-color: #819783;
                    color: rgb(70, 70, 70);
                    transition: .2s ease;
                    
                  &:hover {
                    background-color: #81ae85;
                  }

                }
            }
        }
    }

    &__right {
        position: relative;
        width: 65%;
        background-color: #ecddd6;

        .recipe-image {
            width: 100%;
            height: 40%;
            background-repeat: no-repeat;
            background-size: cover;
            background-position: center;
            position: relative;
    
            &::before {
                content: "";
                position: absolute;
                top: 0;
                right: 0;
                bottom: 0;
                left: 0;
                background: linear-gradient(
                  to right bottom,
                  #131a5b50,
                  #0707b6d3
                );
                opacity: 0.7;
            }

            h2 {
              width: fit-content;
              font-size: 1em; /* Base font size for scaling */
              position: relative;
              top: 10px;
              padding: .3125rem;
              text-transform: uppercase;
              color: #dfd6c1;
              background-color: #4f6f52c5; /* Replace with your desired background color */
              padding: auto 1.25rem;
              border-bottom: .125rem solid #86b585;
          }
          
          h2::after {
              content: "";
              position: absolute;
              top: 0;
              right: -2.0625rem; /* Adjust the em value as needed */
              height: .75rem;
              width: 1.75rem;
              // margin-left: 5rem;
              // border-left: 0.375em solid transparent;
              border-right: 2.0625rem solid transparent;
              border-top: 1.75rem solid #4f6f52c5 /* Same background color as h2 */
          }
          
          
          
          
        }
        .headline {
            width: 100%;
            height: 40%;
            background-image: linear-gradient(rgba(0, 0, 0, 0.372), rgba(0, 0, 0, 0.879)), url(/app/images/photo_2023-11-26_14-50-01.jpg);
            background-repeat: no-repeat;
            background-size: cover;
            background-position: center;
            position: relative;
        }

        .recipe {
            position: absolute;
            bottom: 0;
            background-color: #ECE3CE;
            width: 100%;
            height: 65%;
            border-radius: 2.5rem 2.5rem 0 0;
            padding: 1.5625rem;
            overflow: scroll;
            ::-webkit-scrollbar {
                display: none;
            }
        }
        .group-reporter {
            text-align: center;
            color: rgb(161, 161, 255);
            padding: 6.25rem;
            padding-top: 6.5625rem;
            background-size: cover;
            background-position: center;
            
            h1 {
              line-height: 1;
              margin: 0;
              // font-size: 1.625rem;
              text-shadow: .1875rem .1875rem .1875rem rgba(0, 0, 0, 0.5);
            }
            
            span {
              font-size: .875rem;
              margin: 0;
              text-shadow: none;
              font-weight: lighter;
              text-transform: uppercase;
            }
          }
    }
}

.recipe {
    color: rgb(70, 70, 70);
    &__ingredients {
        // padding: 5rem 8rem;
        font-size: .975rem;
        line-height: 1.4;
        display: flex;
        flex-direction: column;
        align-items: center;
      }
    
      &__ingredient-list {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 10px 19px;
        list-style: none;
      }
    
      &__ingredient {
        display: flex;
      }
    
      &__icon {
        height: 32px;
        width: 32px;
        margin-right: 17.6px;
        flex: 0 0 auto;
        margin-top: 1.6px;
      }
    
      &__quantity {
        margin-right: 8px;
        flex: 0 0 auto;
      }


      svg {
        height: 19.2px;
        width: 19.2px;
        fill: rgb(70, 70, 70);
      }
    
}

.spinner {
    margin: 80px auto;
    text-align: center;
  
    svg {
      height: 96px;
      width: 96px;
      fill: #303047;
      animation: rotate 2s infinite linear;
    }
  }
  
  @keyframes rotate {
    0% {
      transform: rotate(0);
    }
  
    100% {
      transform: rotate(360deg);
    }
  }

  .preview {
    // background-color: red;
    line-height: 1.5;
    &__link {
      &:link,
      &:visited {
        display: flex;
        align-items: center;
        // padding: 24px 52px;
        transition: all 0.3s;
        // border-right: .0625rem solid #fbe8df;
        text-decoration: none;
        border-radius: 3px;
      }
  
      &:hover {
        background-color: #3c468e1e;
        transform: translateY(-0.125rem);
      }
  
      &--active {
        background-color: #3c468e4d;
      }
    }
  
    &__fig {
      flex: 0 0 48px; /* Adjust the size as needed */
      border-radius: 50%;
      overflow: hidden;
      position: relative;
      height: 48px;
      backface-visibility: hidden;
      margin: .625rem;
      
      &::before {
        content: '';
        display: block;
        height: 100%;
        width: 100%;
        position: absolute;
        top: 0;
        left: 0;
        background: linear-gradient(
          to right bottom,
          #131a5b50,
          #0707b6d3
        );
        opacity: 0.4;
      }
  
      img {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: cover;
        transition: all 0.3s;
      }
    }
  
    &__data {
      display: grid;
      width: 100%;
      grid-template-columns: 1fr 32px;
      row-gap: 1.6px;
      align-items: center;
    }
  
    &__title {
      grid-column: 1/-1;
      font-size: .775rem;
      color: #61679f;
      text-transform: uppercase;
      font-weight: 600;
  
      text-overflow: ellipsis;
      max-width: 400px;
      white-space: nowrap;
      overflow: hidden;
      margin: 0;
    }
  
    &__publisher {
      font-size: .6563rem;
      color: #918581;
      text-transform: uppercase;
      font-weight: 600;
      margin: 0;
    }
  
    &__user-generated {
      background-color: darken(#f2efee, 2%);
  
      display: flex;
      align-items: center;
      justify-content: center;
      height: 2rem;
      width: 2rem;
      border-radius: 160px;
  
      margin-left: auto;
      margin-right: 28px;
  
      svg {
        height: 1.2rem;
        width: 1.2rem;
        fill: #f38e82;
      }
    }
  }
  
  .search-results {
    // background-color: blue;
    display: flex;
    flex-direction: column;
  }
  
  .results {
    // background-color: green;
    list-style: none;
    padding: 0;
  }
  
  .pagination {
    margin-top: auto;
    padding: 0 3.5rem;
    position: absolute;
    bottom: 20px;
    width: 90%;
  
    &::after {
      content: '';
      display: table;
      clear: both;
    }
  
    &__btn {
      &--prev {
        float: left;
      }
      &--next {
        float: right;
      }
    }
  }


